INSERT INTO lessons (unit_id, lesson_number, title, objectives, lesson_structure, homework, materials) VALUES
((SELECT id FROM course_units WHERE unit_number = 4 AND course_id = (SELECT id FROM courses WHERE slug = 'super-safari-1')), 1, 'Meeting the Star family', 'приветствия; имена персонажей; числа/цвета', '0–5: ДЗ-чек/повтор (имена, цвета). 5–15: Разминка — ball name game; Star family (картинка/слайд), числа/цвета. 30–50: Практика — bingo (числа/цвета). Коммуникативное — песня «Hello» + жесты; мини-диалоги «My name is». сопоставить; объяснить ДЗ.', 'AB – раскрасить лист; выучить имена персонажей', 'PB Unit 1; AB Unit 1; TB Unit 1; Audio (song); KB1 интерактив'),
((SELECT id FROM course_units WHERE unit_number = 4 AND course_id = (SELECT id FROM courses WHERE slug = 'super-safari-1')), 2, 'Family members introduction', 'семья Leo; слова: mummy, daddy, family', '0–5: ДЗ-чек (имена персонажей). 5–15: Разминка — song «Hello»; повтор имен. 15–35: Презентация — семья Leo (картинка); новые слова mummy/daddy. 35–55: Практика — matching activity; role play family. 55–60: ДЗ + итоги', 'AB p.10; выучить слова family', 'PB p.8-9; AB p.10; Audio track 2'),
((SELECT id FROM course_units WHERE unit_number = 4 AND course_id = (SELECT id FROM courses WHERE slug = 'super-safari-1')), 3, 'Family song and activities', 'песня про семью; повторение family words', '0–5: ДЗ-чек (слова семьи). 5–15: Разминка — name the family members. 15–30: Презентация — family song с движениями. 30–50: Практика — sing along; family tree activity. 50–60: Игра "Where is mummy?" + ДЗ', 'AB p.11; спеть песню дома', 'PB p.10; AB p.11; Audio family song'),
((SELECT id FROM course_units WHERE unit_number = 4 AND course_id = (SELECT id FROM courses WHERE slug = 'super-safari-1')), 4, 'Big and small family', 'понятия big/small; описание семьи', '0–5: ДЗ-чек (семейная песня). 5–15: Разминка — family song with actions. 15–30: Презентация — big family/small family (картинки). 30–50: Практика — sorting game big/small; describe your family. 50–60: Craft time + объяснение ДЗ', 'AB p.12; нарисовать свою семью', 'PB p.11; AB p.12; Craft materials'),
((SELECT id FROM course_units WHERE unit_number = 4 AND course_id = (SELECT id FROM courses WHERE slug = 'super-safari-1')), 5, 'Family revision and games', 'повторение темы семья; закрепление', '0–5: ДЗ-чек (рисунки семьи). 5–15: Разминка — show and tell family pictures. 15–35: Практика — family bingo; memory game family words. 35–55: Коммуникативное — family role play; interview classmates. 55–60: Wrap up + ДЗ', 'AB p.13; повторить все слова unit 4', 'PB p.12; AB p.13; Bingo cards'),
((SELECT id FROM course_units WHERE unit_number = 4 AND course_id = (SELECT id FROM courses WHERE slug = 'super-safari-1')), 6, 'Family project presentation', 'презентация проектов о семье; итоговое повторение', '0–5: Подготовка презентаций. 5–25: Презентации детей о своих семьях. 25–45: Групповая игра — family charades; guess the family member. 45–60: Celebration time; stickers; подведение итогов юнита', 'Повторить Unit 4; подготовиться к Unit 5', 'Presentation materials; Stickers; Certificates');